var authors = require('../controllers/authors.js');

module.exports = function(app){
    app.get('/authors', function (req, res) {
        authors.index(req, res);
    })
    app.get('/authors/:id', function (req, res) {
        authors.getAuthor(req, res);
    })
    app.post('/authors', function (req, res) {
        authors.newAuthor(req, res);
    })
    app.post('/authors/:id', function (req, res) {
        authors.editAuthor(req, res);
    })
    app.delete('/authors/:id', function (req, res) {
        authors.deleteAuthor(req, res);
    })
}
