var cakes = require('../controllers/cakes.js');

module.exports = function(app){
    app.get('/cakes', function (req, res) {
        cakes.index(req, res);
    })
    app.get('/cakes/:id', function (req, res) {
        cakes.showCake(req, res);
    })
    app.post('/cakes', function (req, res) {
        cakes.newCake(req, res);
    })
    app.put('/cakes/:id', function (req, res) {
        cakes.editCake(req, res);
    })
    app.delete('/cakes/:id', function (req, res) {
        cakes.removeCake(req, res);
    })
    app.post('/cakes/:id/rate', function (req, res) {
        cakes.rateCake(req, res);
    })
    // app.put('/cakes/:id/rate', function (req, res) {
    //     cakes.rateCake(req, res);
    // })
}
