var mongoose = require('mongoose');

var TaskSchema = new mongoose.Schema({
    title: String,
    description: {type: String, default: '' },
    // title: {type: String, required: [true, "A title is required"], minlength: [2, "Titles must have at least 2 characters"]},
    // description: { type: String, required: [true, "A description is required"], minlength: [2, "Descriptions must have at least 2 characters"]},
    completed: {type: Boolean, default: false },
}, { timestamps: { createdAt: 'created_at', updatedAt: 'updated_at' }});
var Task = mongoose.model('Task', TaskSchema);
module.exports = Task